mapper.update:

	UPDATE boards
	SET board_title = #{boardTitle}
  	  , board_content = #{boardContent}
      , update_dt = SYSDATE
	WHERE board_no = #{boardNo}
	AND   mem_id = #{memId}


UPDATE boards
SET use_yn = 'N'
WHERE board_no = #{boardNo}
AND   mem_id = #{memId}


UPDATE replys
SET use_yn = 'N'
WHERE reply_no = #{replyNo}
